import React from "react";
import homeOne from '../assets/Images/Brew-Story/stat1.jpg';
import homeTwo from '../assets/Images/Brew-Story/ban2.png';
// import homeThree from '../assets/Images/Brew-Story/ban4.jpg';
// import homeTwo from "/src/assets/Images/Brew-Story/staticHome2.jpg";

function StaticHomeHeader(props) {
  return (
    <div>
     <div className="container-fluid">
        <div className="row">
            <div className="col-sm-8">
                <img src={homeOne} className="img-fluid rounded-5 shadow-lg p-3 mb-5 bg-body rounded" alt="" />
            </div>
            
            <div className="col-sm-3 ms-auto me-auto text-center mytext shadow p-3 mb-5 bg-body rounded-5">
               <h2 className="mt-3 mytext">Let's Create Your</h2>
               <h1 className="mt-1">Brew Story!</h1>
               <p>Embark on a tantalizing journey through the rich tapestry of flavors at Brew Story, where every sip narrates a tale of craftsmanship and passion. Immerse yourself in a world where the aromatic symphony of carefully selected ingredients dances on your palate, leaving an indelible mark on your taste buds.</p>
            </div>
        </div>


        <div className="row">
            <div className="col-md-12">
            <div class="card mb-3" style="max-width: 540px;">
  <div class="row g-0">
    <div class="col-md-4">
      <img src="..." class="img-fluid rounded-start" alt="..." />
    </div>
    <div class="col-md-8">
      <div class="card-body">
        <h5 class="card-title">Card title</h5>
        <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
        <p class="card-text"><small class="text-body-secondary">Last updated 3 mins ago</small></p>
      </div>
    </div>
  </div>
</div>
            </div>
        </div>
     </div>
    </div>

    
  );
}

export default StaticHomeHeader;
