import React, { useState } from "react";
import { Navigate, resolvePath, useNavigate } from "react-router-dom";

function Register(props) {

  const [formData, setFormData] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange=(e)=>{
      const {name, value} = e.target;

      setFormData((prev)=> ({
        ...prev, [name]: value
      }))
  }


  const submitForm = async (e) => {
    e.preventDefault();

    const config = {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify(formData)
    }

    let checkUser = await fetch(`http://localhost:5000/user?email${formData.email}`, {method: GET})

    if(checkUser.ok){
      let user = JSON.parse(checkUser)
      if(user.handleChange > 0){
        setMessage('User Already Exist')
      } else {

        let response = await fetch(`http://localhost:5000/user`, config);

        if (response.status === 201) {
          let user = await response.parse();

          localStorage.setItem('brewUser', JSON.stringify(user));
          
          setTimeout(() => {
            navigate('/account/login')
          }, 1000);

          setMessage('User Registered Succefully')
        } else {
          setMessage('Something went wrong')
        }


      }
    } else {
      setMessage('Something went wrong, Please try again')
    }





  }





  return (
    <div>
      <form className="navColor p-3 rounded-2">
      <h1 className="mytext mb-4">Hey! Welcome to Brew Story</h1>
        <div className="mb-3">
          <label for="exampleInputText" className="form-label">
            Full Name
          </label>
          <input
          onChange={handleChange}
            type="text"
            className="form-control"
            id="exampleInputText"
            aria-describedby="emailHelp"
            name="fullname"
          />
        </div>
        <div className="mb-3">
          <label for="exampleInputEmail1" className="form-label">
            Email address
          </label>
          <input 
          onChange={handleChange}
            type="email"
            name="email"
            className="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
          />
          <div id="emailHelp" className="form-text">
            We'll never share your email with anyone else.
          </div>
        </div>
        <div className="mb-3">
          <label for="exampleInputPassword1" className="form-label">
            Password
          </label>
          <input
           onChange={handleChange}
            type="password"
            name="password"
            className="form-control"
            id="exampleInputPassword1"
          />
        </div>
        <div className="mb-3">
          <label for="exampleInputPassword2" className="form-label">
            Re Enter Your Password
          </label>
          <input
          onChange={handleChange}
            type="password"
            className="form-control" name="reEnterPassword"
            id="exampleInputPassword2"
          />
        </div>
        
        <button type="submit" onClick={submitForm} className="btn btn-outline-dark mb-3">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Register;
