import { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";

const AuthContext = useContext();

export const AuthProvider = ({ children }) => {
  const [formData, setFormData] = useState("");
  const [user, setUser] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  //register code
  const register = async (e) => {
    e.preventDefault();

    const config = {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(formData),
    };

    let checkUser = await fetch(
      `http://localhost:5000/user?email=${formData.email}`,
      { method: "GET" }
    );

    if (checkUser.ok) {
      let user = await checkUser.json();
      if (user.length > 0) {
        setMessage("User Already Exist");
      } else {
        let response = await fetch(`http://localhost:5000/user`, config);

        if (response.status === 201) {
          let user = await response.json();

          localStorage.setItem("brewUser", JSON.stringify(user));

          setTimeout(() => {
            navigate("/account/login");
          }, 1500);

          setMessage("User Registered Succefully");
        } else {
          setMessage("Something went wrong");
        }
      }
    } else {
      setMessage("Something went wrong, Please try again");
    }
  };

  //login code
  const login = async (e) => {
    e.preventDefault();

    let response = await fetch (`http://localhost:5000/user?email=${formData.email}&password=${formData.password}&name=${formData.name}`, {method: 'GET'});

    let user = await response.json();

    if (user.length > 0) {
      setMessage('Login Successful')
      localStorage.setItem('brewUser', JSON.stringify(user))

      setTimeout(() => {
        navigate('/home')
      },1500);

    } else {
      setMessage('Something went wrong, Please try again')
    }
}

  return (
    <AuthContext.Provider
      value={{
        login,
        register,
        setMessage,
        message,
        user,
        setUser
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
