import React from "react";
import homeOne from '../assets/Images/Brew-Story/stat1.jpg';
import homeTwo from '../assets/Images/Brew-Story/ban2.png';
// import homeThree from '../assets/Images/Brew-Story/ban4.jpg';
// import homeTwo from "/src/assets/Images/Brew-Story/staticHome2.jpg";

function StaticHomeHeader(props) {
  return (
    <div>
     <div className="container-fluid">
        <div className="row">
            <div className="col-sm-8">
                <img src={homeOne} className="img-fluid rounded-5" alt="" />
            </div>
            
            <div className="col-sm-4 text-center">
               <h2 className="textColor">Let's Create Your</h2>
               <h1 className="textColor">Brew Story!</h1>
               <p className="text-center textColor">Embark on a tantalizing journey through the rich tapestry of flavors at Brew Story, where every sip narrates a tale of craftsmanship and passion. Immerse yourself in a world where the aromatic symphony of carefully selected ingredients dances on your palate, leaving an indelible mark on your taste buds.</p>
            </div>
        </div>
     </div>
    </div>
  );
}

export default StaticHomeHeader;
